import React from 'react'
import SVG from 'react-inlinesvg'
import  {scale} from "./Helpers";
import styled, { css, keyframes } from "styled-components";
import Sketch from 'react-p5';
import {objectData} from "./Data"
export class EachCountry extends React.Component{
    constructor(props){
        super(props);
        this.state ={isSelected:false};
        this.handleSelected = this.handleSelected.bind(this);
    }

    handleSelected(){
        this.setState((prevState)=>{
            return {isSelected: !prevState.isSelected};
        });
    }

    render(){
        const svgPath = `https://storage.googleapis.com/ceramics/assets/${this.props.isoCode}.svg`;
        const width = this.props.width;
        const height = this.props.height;
        const lat = this.props.latLon[0];
        const lon = this.props.latLon[1];
        const count = this.props.objectCount;
        const country = this.key;
        const {isSelected} = this.state;
        // console.log(objectIDs)

        let colors;
        if (isSelected){
            colors = (
                <Sketch
                    setup={(p5, parent)=>{
                        p5.createCanvas(width-40, 20*3).position(20,height-15);
                    }}
                    draw={p5=>{
                        p5.background(0,0);
                        const side=(width-40)/this.props.objectCount;
                        for (let i=0; i<this.props.objectList.len; i++){
                            let obj = objectData.find(function(e){ return e.objectID===this.props.objectList[i]})
                            p5.fill(obj.colors[0]);
                            p5.rect(i*side, obj.percents[0]*60, side, side)
                            p5.fill(obj.colors[1]);
                            p5.rect(i*side, (obj.percents[0]+obj.percents[1])*60, side, side)
                            p5.fill(obj.colors[2]);
                            p5.rect(i*side, (obj.percents[0]+obj.percents[1]+obj.percents[2]*60), side, side)
                        }
                    }} />
            )
        }
        const sizeScale=scale(this.props.size, 2180,3600950,20,500)
        const countryStyle = {
            opacity: scale(this.props.objectCount,1,2616,0.1,1),
            top: Math.round(scale(-lat, -56.264,40.901,height/10, height/10*9)),
            left: Math.round(scale(lon, -102.553,174.886,height/10, width-height/10)),
            width: sizeScale,
            height: sizeScale,
            position: 'absolute'
        };
        const fillColor = isSelected?"#A6807A":"#384e68"
        const floating = keyframes`
            from {-webkit-transform:translate(0, 0px);}
            65% {-webkit-transform:translate(0, 50px);}
            to {-webkit-transform: translate(0, -0px);}`;
        const spinning = keyframes`
            0% {-webkit-transform: rotate(0deg);}
            100% {-webkit-transform: rotate(360deg);}`;
        const Float = styled.div`
            animation: ${floating} 4s easy-in-out infinite`;



        return(
                <div>
                    <SVG className="float" src={svgPath} onMouseEnter={this.handleSelected} onMouseLeave={this.handleSelected} style={countryStyle} uniquifyIDs={true} preProcessor={code => code.replace(/fill=".*?"/g, `fill=${fillColor}`)}/>

                    {colors}
                </div>

        )
    }

}
